import {
  ADD_DATA_SUCCESS,
  FETCH_USER_DATA,
  DELETE_USER_DATA
} from "../actions/types";

const initState = {
  data: []
};

const userReducer = (state = initState, action) => {
  switch (action.type) {
    case ADD_DATA_SUCCESS:
      const userData = action.payload;
      let newArr;
      if (action.actionType === "UPDATE") {
        newArr = {
          ...state.data,
          [userData.id]: userData
        };
      } else {
        newArr = state.data.concat(action.payload);
      }

      return {
        ...state,
        data: newArr,
        userSaved: true
      };

    case FETCH_USER_DATA:
      const users = {
        ...state,
        userSaved: false
      };
      return users;

    case DELETE_USER_DATA:
      return {
        ...state,
        data: state.data.filter(({ id }) => !action.payload.includes(id))
      };

    default:
      return state;
  }
};

export default userReducer;
